import type { Fruit } from "@/registry/blocks/example-fruits/lib/fruits";

import { useEffect, useState } from "react";

import Fruits from "@/registry/blocks/example-fruits/components/fruits";
import { getFruitList } from "@/registry/blocks/example-fruits/lib/fruits";
import "./page.css";

export default function ExampleFruits() {
  const [fruits, setFruits] = useState<Fruit[]>([]);

  useEffect(() => {
    setFruits(getFruitList());
  }, []);

  return <Fruits items={fruits} />;
}

import { Card, CardBody, CardFooter, Image } from "@heroui/react";

export default function Fruits({
  items = [],
}: {
  items: { title: string; img: string; price: string }[];
}) {
  return (
    <div className="gap-2 grid grid-cols-2 sm:grid-cols-4">
      {items.map((item, index) => (
        <Card key={index} isPressable shadow="sm">
          <CardBody className="overflow-visible p-0">
            <Image
              alt={item.title}
              className="w-full object-cover h-[140px]"
              radius="lg"
              shadow="sm"
              src={item.img}
              width="100%"
            />
          </CardBody>
          <CardFooter className="text-small justify-between">
            <b>{item.title}</b>
            <p className="text-default-500">{item.price}</p>
          </CardFooter>
        </Card>
      ))}
    </div>
  );
}

export function useFruits(number: number) {
  return number;
}

export type Fruit = {
  title: string;
  img: string;
  price: string;
};

export function getFruitList(): Fruit[] {
  return [
    {
      title: "Orange",
      img: "/images/fruit-1.jpeg",
      price: "$5.50",
    },
    {
      title: "Tangerine",
      img: "/images/fruit-2.jpeg",
      price: "$3.00",
    },
    {
      title: "Raspberry",
      img: "/images/fruit-3.jpeg",
      price: "$10.00",
    },
    {
      title: "Lemon",
      img: "/images/fruit-4.jpeg",
      price: "$5.30",
    },
    {
      title: "Avocado",
      img: "/images/fruit-5.jpeg",
      price: "$15.70",
    },
    {
      title: "Lemon 2",
      img: "/images/fruit-6.jpeg",
      price: "$8.00",
    },
    {
      title: "Banana",
      img: "/images/fruit-7.jpeg",
      price: "$7.50",
    },
    {
      title: "Watermelon",
      img: "/images/fruit-8.jpeg",
      price: "$12.20",
    },
  ];
}
